CREATE TABLE IF NOT EXISTS users (UserId INT UNSIGNED NOT NULL AUTO_INCREMENT,UserName VARCHAR(60),UserEmail VARCHAR(120),PRIMARY KEY(UserId))
CREATE TABLE IF NOT EXISTS urls (UrlId INT UNSIGNED NOT NULL AUTO_INCREMENT,Url VARCHAR(255),Type VARCHAR(5),PRIMARY KEY(UrlId))
CREATE TABLE IF NOT EXISTS pdfs (PdfId INT UNSIGNED NOT NULL AUTO_INCREMENT,LocationDir VARCHAR(255),DlDate TIMESTAMP,UrlId INT UNSIGNED NOT NULL,PRIMARY KEY(PdfId), FOREIGN KEY(UrlId) REFERENCES urls(UrlId) ON DELETE CASCADE)
CREATE TABLE IF NOT EXISTS rss (RssId INT UNSIGNED NOT NULL AUTO_INCREMENT,FeedTitle VARCHAR(255),FeedDes TEXT,LinkTitle VARCHAR(255),LinkDes Text,LinkPubDate VARCHAR(30),LinkLink VARCHAR(255),DlDate TIMESTAMP,UrlId INT UNSIGNED NOT NULL,PRIMARY KEY(RssId), FOREIGN KEY(UrlId) REFERENCES urls(UrlId) ON DELETE CASCADE)
CREATE TABLE IF NOT EXISTS html (HtmlId INT UNSIGNED NOT NULL AUTO_INCREMENT,html TEXT,DlDate TIMESTAMP,UrlId INT UNSIGNED NOT NULL,PRIMARY KEY(HtmlId), FOREIGN KEY(UrlId) REFERENCES urls(UrlId) ON DELETE CASCADE)
CREATE TABLE IF NOT EXISTS text (TextId INT UNSIGNED NOT NULL AUTO_INCREMENT,text TEXT,HtmlId INT UNSIGNED NOT NULL,PRIMARY KEY(TextId), FOREIGN KEY(HtmlId) REFERENCES html(HtmlId) ON DELETE CASCADE)